    <span class="hljs-comment">// Prepare the SELECT statement</span>
    <span class="hljs-variable">$stmt</span> = <span class="hljs-variable">$pdo</span>-&gt;<span class="hljs-title function_ invoke__">prepare</span>(<span class="hljs-string">"SELECT * FROM people_team_startups"</span>);
    <span class="hljs-variable">$stmt</span>-&gt;<span class="hljs-title function_ invoke__">execute</span>();

    <span class="hljs-comment">// Fetch all the records</span>
    <span class="hljs-variable">$people</span> = <span class="hljs-variable">$stmt</span>-&gt;<span class="hljs-title function_ invoke__">fetchAll</span>(PDO::<span class="hljs-variable constant_">FETCH_ASSOC</span>);
} <span class="hljs-keyword">catch</span> (PDOException <span class="hljs-variable">$e</span>) {
