);

<span class="hljs-keyword">INSERT</span> <span class="hljs-keyword">INTO</span> people_team_startups (id, startup_id, Name, Role, Degree_uni)
  <span class="hljs-keyword">SELECT</span> id, startup_id, Name, Role, Degree_uni <span class="hljs-keyword">FROM</span> old_table;

