4 7 4H8.53513ZM8 6H7C6.44772 6 6 6.44772 6 7V19C6 19.5523 6.44772 20 7 20H17C17.5523 20 18 19.5523 18 19V7C18 6.44772 17.5523 6 17 6H16C16 7.10457 15.1046 8 14 8H10C8.89543 8 8 7.10457 8 6Z" fill="currentColor"></path></svg>Copy code</button></span></div><div class="p-4 overflow-y-auto"><code class="!whitespace-pre hljs language-sql"><span class="hljs-comment">-- Rename the old table</span>
<span class="hljs-keyword">ALTER</span> <span class="hljs-keyword">TABLE</span> startups_information RENAME <span class="hljs-keyword">TO</span> old_startups_info;

<span class="hljs-comment">-- Create the new table with the additional `about_team` column</span>
<span class="hljs-keyword">CREATE</span> <span class="hljs-keyword">TABLE</span> startups_information (
  startup_id <span class="hljs-type">INTEGER</span> <span class="hljs-keyword">PRIMARY</span> KEY,
  startup_name TEXT,
  industry TEXT,
  location TEXT,
  vision TEXT,
  usp TEXT,
  motivation TEXT,
  target_market TEXT,
  customer_num <span class="hljs-type">INTEGER</span>,
  revenue_till_date <span class="hljs-type">INTEGER</span>,
  Team_size <span class="hljs-type">INTEGER</span>,
  Founding_year <span class="hljs-type">INTEGER</span>,
  logo TEXT,
  Funding_stage TEXT,
  Customer_acc_cost <span class="hljs-type">INTEGER</span>,
  Retention_rate_perc <span class="hljs-type">INTEGER</span>,
  User_growth_perc <span class="hljs-type">INTEGER</span>,
  about_team TEXT, <span class="hljs-comment">-- New column for team description</span>
  <span class="hljs-keyword">FOREIGN</span> KEY (startup_id) <span class="hljs-keyword">REFERENCES</span> login(id)
);

<span class="hljs-comment">-- Copy the data from the old table to the new table</span>
<span class="hljs-keyword">INSERT</span> <span class="hljs-keyword">INTO</span> startups_information (
  startup_id,
  startup_name,
  industry,
  location,
  vision,
  usp,
  motivation,
  target_market,
  customer_num,
  revenue_till_date,
  Team_size,
  Founding_year,
  logo,
  Funding_stage,
  Customer_acc_cost,
  Retention_rate_perc,
  User_growth_perc
  <span class="hljs-comment">-- Note that 'about_team' is not included here since it's a new column</span>
)
<span class="hljs-keyword">SELECT</span>
  startup_id,
  startup_name,
  industry,
  location,
  vision,
  usp,
  motivation,
  target_market,
  customer_num,
  revenue_till_date,
  Team_size,
  Founding_year,
  logo,
  Funding_stage,
  Customer_acc_cost,
  Retention_rate_perc,
  User_growth_perc
<span class="hljs-keyword">FROM</span> old_startups_info;

