        $customer_num = filter_input(INPUT_POST, 'nocustomers', FILTER_SANITIZE_NUMBER_INT);
        $revenue_till_date = filter_input(INPUT_POST, 'revenue', FILTER_SANITIZE_NUMBER_INT);
