        $user_growth_perc = filter_input(INPUT_POST, 'user_growth_percentage', FILTER_SANITIZE_NUMBER_INT);
        $about_team = filter_input(INPUT_POST, 'abt_team', FILTER_SANITIZE_STRING);
        
        // Handle the logo upload
        $logoPath = null;
