    <span class="hljs-keyword">CASE</span>
        <span class="hljs-keyword">WHEN</span> <span class="hljs-type">DATE</span>(m.timestamp) <span class="hljs-operator">=</span> <span class="hljs-type">DATE</span>(<span class="hljs-string">'now'</span>) <span class="hljs-keyword">THEN</span> <span class="hljs-string">'Today'</span>
        <span class="hljs-keyword">WHEN</span> <span class="hljs-type">DATE</span>(m.timestamp) <span class="hljs-operator">=</span> <span class="hljs-type">DATE</span>(<span class="hljs-string">'now'</span>, <span class="hljs-string">'-1 day'</span>) <span class="hljs-keyword">THEN</span> <span class="hljs-string">'Yesterday'</span>
        <span class="hljs-keyword">ELSE</span> printf(<span class="hljs-string">'%d days ago'</span>, (julianday(<span class="hljs-string">'now'</span>) <span class="hljs-operator">-</span> julianday(<span class="hljs-type">DATE</span>(m.timestamp))))
    <span class="hljs-keyword">END</span> <span class="hljs-keyword">as</span> readable_date,
    l.AccountType <span class="hljs-keyword">as</span> other_person_type,
    <span class="hljs-keyword">CASE</span> 
        <span class="hljs-keyword">WHEN</span> l.AccountType <span class="hljs-operator">=</span> <span class="hljs-string">'startup'</span> <span class="hljs-keyword">THEN</span> si.logo
        <span class="hljs-keyword">WHEN</span> l.AccountType <span class="hljs-operator">=</span> <span class="hljs-string">'investor'</span> <span class="hljs-keyword">THEN</span> ii.image
        <span class="hljs-keyword">ELSE</span> <span class="hljs-keyword">NULL</span>
    <span class="hljs-keyword">END</span> <span class="hljs-keyword">as</span> person_image_path,
    l.name <span class="hljs-keyword">as</span> other_person_name
<span class="hljs-keyword">FROM</span> 
