        <span class="hljs-variable">$stmt</span> = <span class="hljs-variable">$pdo</span>-&gt;<span class="hljs-title function_ invoke__">prepare</span>(<span class="hljs-variable">$sql</span>);
        <span class="hljs-variable">$stmt</span>-&gt;<span class="hljs-title function_ invoke__">execute</span>([<span class="hljs-variable">$investorId</span>, <span class="hljs-variable">$location</span>, <span class="hljs-variable">$currentRole</span>, <span class="hljs-variable">$yourStory</span>, <span class="hljs-variable">$yourExperience</span>, <span class="hljs-variable">$yourEducation</span>, <span class="hljs-variable">$yourExpertise</span>, <span class="hljs-variable">$imagePath</span>, <span class="hljs-variable">$fundingPref</span>]);
        
