        <span class="hljs-tag">&lt;<span class="hljs-name">button</span> <span class="hljs-attr">class</span>=<span class="hljs-string">"p-2 rounded-full bg-green-500 text-white"</span> <span class="hljs-attr">id</span>=<span class="hljs-string">"run"</span>&gt;</span>
