<span class="hljs-tag">&lt;/<span class="hljs-name">div</span>&gt;</span>

<span class="hljs-comment">&lt;!-- Swipe Buttons --&gt;</span>
<span class="hljs-tag">&lt;<span class="hljs-name">div</span> <span class="hljs-attr">class</span>=<span class="hljs-string">"w-full max-w-2xl mx-auto p-4 flex items-center justify-between bg-[#110B11] rounded-lg shadow"</span>&gt;</span>
    <span class="hljs-tag">&lt;<span class="hljs-name">button</span> <span class="hljs-attr">class</span>=<span class="hljs-string">"p-2 rounded-full bg-red-500 text-white"</span> <span class="hljs-attr">id</span>=<span class="hljs-string">"reset"</span>&gt;</span>Swipe Left<span class="hljs-tag">&lt;/<span class="hljs-name">button</span>&gt;</span>
    <span class="hljs-tag">&lt;<span class="hljs-name">button</span> <span class="hljs-attr">class</span>=<span class="hljs-string">"p-2 rounded-full bg-green-500 text-white"</span> <span class="hljs-attr">id</span>=<span class="hljs-string">"run"</span>&gt;</span>Swipe Right<span class="hljs-tag">&lt;/<span class="hljs-name">button</span>&gt;</span>
<span class="hljs-tag">&lt;/<span class="hljs-name">div</span>&gt;</span>

<span class="hljs-tag">&lt;<span class="hljs-name">script</span>&gt;</span><span class="javascript">
    <span class="hljs-comment">// Example card data array</span>
    <span class="hljs-keyword">var</span> cards = [
        {
            <span class="hljs-attr">title</span>: <span class="hljs-string">"Card 1"</span>,
            <span class="hljs-attr">content</span>: <span class="hljs-string">"This is the content of card 1."</span>
        },
        {
            <span class="hljs-attr">title</span>: <span class="hljs-string">"Card 2"</span>,
            <span class="hljs-attr">content</span>: <span class="hljs-string">"This is the content of card 2."</span>
        },
        {
            <span class="hljs-attr">title</span>: <span class="hljs-string">"Card 3"</span>,
            <span class="hljs-attr">content</span>: <span class="hljs-string">"This is the content of card 3."</span>
        }
        <span class="hljs-comment">// Add more cards as needed</span>
    ];

    <span class="hljs-keyword">var</span> currentCardIndex = <span class="hljs-number">0</span>;

    <span class="hljs-keyword">function</span> <span class="hljs-title function_">loadCard</span>(<span class="hljs-params"></span>) {
        <span class="hljs-keyword">if</span> (currentCardIndex &lt; cards.<span class="hljs-property">length</span>) {
            <span class="hljs-keyword">var</span> card = cards[currentCardIndex];
            <span class="hljs-keyword">var</span> cardContent = <span class="hljs-string">`
                &lt;div class="p-4 text-white"&gt;
                    &lt;h2 class="text-lg font-bold"&gt;<span class="hljs-subst">${card.title}</span>&lt;/h2&gt;
                    &lt;p&gt;<span class="hljs-subst">${card.content}</span>&lt;/p&gt;
                &lt;/div&gt;
            `</span>;
            <span class="hljs-variable language_">document</span>.<span class="hljs-title function_">querySelector</span>(<span class="hljs-string">'.wrapper'</span>).<span class="hljs-property">innerHTML</span> = cardContent;
            currentCardIndex++;
        } <span class="hljs-keyword">else</span> {
            <span class="hljs-comment">// No more cards available</span>
            <span class="hljs-variable language_">document</span>.<span class="hljs-title function_">querySelector</span>(<span class="hljs-string">'.wrapper'</span>).<span class="hljs-property">innerHTML</span> = <span class="hljs-string">'&lt;p class="text-white text-center"&gt;No more cards available.&lt;/p&gt;'</span>;
        }
    }

    <span class="hljs-variable language_">document</span>.<span class="hljs-title function_">getElementById</span>(<span class="hljs-string">"run"</span>).<span class="hljs-title function_">addEventListener</span>(<span class="hljs-string">"click"</span>, <span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) {
        <span class="hljs-title function_">swipeCard</span>(<span class="hljs-string">'up'</span>);
    });

    <span class="hljs-variable language_">document</span>.<span class="hljs-title function_">getElementById</span>(<span class="hljs-string">"reset"</span>).<span class="hljs-title function_">addEventListener</span>(<span class="hljs-string">"click"</span>, <span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) {
        <span class="hljs-title function_">swipeCard</span>(<span class="hljs-string">'down'</span>);
    });

    <span class="hljs-keyword">function</span> <span class="hljs-title function_">swipeCard</span>(<span class="hljs-params">direction</span>) {
        <span class="hljs-keyword">var</span> wrapper = <span class="hljs-variable language_">document</span>.<span class="hljs-title function_">querySelector</span>(<span class="hljs-string">'.wrapper'</span>);
        wrapper.<span class="hljs-property">classList</span>.<span class="hljs-title function_">add</span>(direction === <span class="hljs-string">'up'</span> ? <span class="hljs-string">'swipe-up'</span> : <span class="hljs-string">'swipe-down'</span>);
        <span class="hljs-built_in">setTimeout</span>(<span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) {
            <span class="hljs-title function_">loadCard</span>();
            wrapper.<span class="hljs-property">classList</span>.<span class="hljs-title function_">remove</span>(<span class="hljs-string">'swipe-up'</span>, <span class="hljs-string">'swipe-down'</span>);
        }, <span class="hljs-number">1000</span>); <span class="hljs-comment">// Reduced timeout for a faster transition</span>
    }

    <span class="hljs-comment">// Load the initial card</span>
    <span class="hljs-title function_">loadCard</span>();
</span><span class="hljs-tag">&lt;/<span class="hljs-name">script</span>&gt;</span>
<span class="hljs-tag">&lt;/<span class="hljs-name">body</span>&gt;</span>
