            <span class="hljs-string">'success'</span> =&gt; <span class="hljs-literal">true</span>,
            <span class="hljs-string">'html'</span> =&gt; <span class="hljs-variable">$html</span>,
            <span class="hljs-string">'lastId'</span> =&gt; <span class="hljs-variable">$newContent</span>[<span class="hljs-string">'investor_id'</span>]
