function swipeCard(direction) {
    var wrapper = document.querySelector('.wrapper');
    if (direction === 'up') {
        wrapper.classList.add('swipe-up');
    } else if (direction === 'down') {
        wrapper.classList.add('swipe-down');
    }
    setTimeout(function() {
        fetchNewContent();
        wrapper.classList.remove('swipe-up', 'swipe-down');
    }, 3000); // Adjust timeout to match animation duration
}

function fetchNewContent() {
    const lastId = document.querySelector('.wrapper').getAttribute('data-last-id') || 0;
    fetch('', { // Current page URL
        method: 'POST',
        headers: {'Content-Type': 'application/x-www-form-urlencoded'},
        body: `fetchNew=true&amp;lastId=${lastId}`
    })
    .then(response =&gt; response.json())
    .then(data =&gt; {
        if(data.success) {
            document.querySelector('.wrapper').innerHTML = data.html;
            document.querySelector('.wrapper').setAttribute('data-last-id', data.lastId);
            window.scrollTo(0, 0); // Scroll to top to view new content
        } else {
            alert('No more content.');
        }
    })
    .catch(error =&gt; console.error('Error:', error));
}

