function fetchNewContent() {
    var lastId = document.getElementById('contentWrapper').getAttribute('data-last-id') || 0;

    fetch(window.location.href, {
        method: 'POST',
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
